where a.state = '00A'

  and a.admin_no = #{admin_no}


  and a.amdin_password = #{password}


  and a.role_id in

        #{item}



  and a.admin_name like concat(concat('%', #{admin_name}), '%')


  and a.shop_no = #{shop_no}

mapper.update:
update admin set admin_name = #{admin_name},admin_tel_num = #{admin_tel_num},
admin_email = #{admin_email},admin_sex = #{admin_sex}
where state = '00A' and admin_no = #{admin_no}
